import React from "react";
import '../App.css';
import { needScale } from '../services/Scales';
// import { tallyScaleItem } from '../services/ProcessScale';

class Profiles extends React.Component {

  state = { 
    needScale: [],
    personalTraits: this.props.traits,
    percent: 1.0,
    past: this.props.past
  };

  componentDidMount() {
    this.setState({ needScale: needScale });
    console.log("TRAITS in Profiles: ", this.state.personalTraits);
  }

  tallyScaleItem = (item) => {
    let result = 0;
    const traits = this.state.personalTraits;
    const ind = traits.filter(value => item.indicative.includes(value)).length;
    const contra = traits.filter(value => item.contraindicative.includes(value)).length;
    const pos = item.indicative.length;
    const neg = item.contraindicative.length;

    if(ind+contra === 0) {
      return (ind + contra / 2) * 100;
    }




    // if((ind === 0) && (contra === 0)) {
    //   return ind + contra / 2;
    // }
    // return (ind - contra / ind + contra) * 50;


    // for(let i = 0; i < traits.length; i++) {
    //   if(item.indicative.includes(traits[i])) {
    //     result++;  
    //   }
    //   if(item.contraindicative.includes(traits[i])) {
    //     result--;  
    //   }
    // }

    // // console.log("IND: ", traits.filter(value => item.indicative.includes(value)).length === 0);
    // // console.log("CONT: ", traits.includes(item.contraindicative));
    // // Set the scale to neutral if no relevant traits have been checked
    // // if(!(traits.includes(item.indicative)) && !(traits.includes(item.contraindicative)) ) {
    // //   return 50;
    // // }
    // return result + item.contraindicative.length;
    // return result; 
  }

  renderLines() {
    const Line = ({left}) => {
      return (
        <div 
          className="line"
          style={{left: `${left}%`}}
        />
      )
    }
    return Array(10).fill(null).map((el, i) => (
      <Line 
        left={i * 10}
        key={i}
      />
    ))
  }

  renderBars() {
    const Bar = ({percent}) => {
      return (
        <div className="bar" style={{width: `${percent}%`}}/>
      )
    }
    // const { needScale } = this.props
    const needScale = this.state.needScale; 

    return needScale.map(trait => {
      const denominator = trait.indicative.length + trait.contraindicative.length;
      // let percent = (this.tallyScaleItem(trait) / denominator ) * 100;
      let percent = this.tallyScaleItem(trait);
      // if(this.tallyScaleItem(trait) === 0) {
      //   percent = 50;
      // }
      return (
        <Bar
          percent={percent}
          key={trait.trait}
        />
      )
    });
  }

  renderBarText() {
    const needScale = this.state.needScale;
    return (
      <div className="bar-text-content">
        {
          needScale.map((trait) => (
            <div className="text" key={trait.trait}>
              {trait.trait}
            </div>
          ))
        }
      </div>
    )
  }

  render() {
    return (
      <div>
        <h1>Personality Profiles</h1>
        <div className="graph-wrapper"> 
          <h2 className="graph-header">Need Scale</h2>
          <p>Assessment of psychological wants or needs</p>         
          <div className="graph">
            {this.renderBarText()}
            <div className="bar-lines-container"> 
              {this.renderLines()}
              {this.renderBars()}
            </div> 
          </div>
        </div>
        <h1>TAT Responses</h1>
        <div className="graph-wrapper">
        <h2 className="graph-header">Picture 31</h2>
          <div className="graph descriptions text-box">

            <div className="clearfix">

              <img src={require(`../images/tat-29.jpg`)} alt="apperception" className="inset" />
              <div >
                <h3>Past</h3>
                <p>A demigod was executed in the Holy Land over two millennia ago. He vowed one day to return to usher in the End of Time.</p>
                <h3>Present</h3>
                <p>He is now reigning down from Heaven in all his glory.</p>
                <h3>Future</h3>
                <p>He will soon sit at the right hand of the Father to judge the quick and the dead.</p>
                <h3>Feelings</h3>
                <p>We are all very afraid: where will YOU spend eternity?</p>
              </div>

            </div>
          </div>
        </div>
      </div>
    );
  }

}

export default Profiles;
